package day6

var input = ""

func good(sig []int32, size int) bool {
	set := make(map[int32]struct{}, 0)
	for _, s := range sig {
		set[s] = struct{}{}
	}
	return len(set) == size
}

func solve(size int) int {
	var sig []int32
	for index, item := range input {
		if index <= size-1 {
			sig = append(sig, item)
		} else {
			if good(sig, size) {
				return index
			}
			sig = append(sig[1:], item)
		}

	}
	panic("no packet was found")
}

func Part1() int {
	return solve(4)
}

func Part2() int {
	return solve(14)
}
